public class Begin11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
float a,b;
a = 4;
b = 5;
float sumAbs = Math.abs(a)+Math.abs(b);
float differenceAbs = Math.abs(a)-Math.abs(b);
float productAbs = Math.abs(a)*Math.abs(b);
float quotientAbs = Math.abs(a)/Math.abs(b);
System.out.println("Сумма модулей равна "+sumAbs);
System.out.println("Разность модулей равна "+differenceAbs);
System.out.println("Произведение модулей равно "+productAbs);
System.out.println("Частное модулей равно "+quotientAbs);
	}

}
